import { useState } from 'react';
import './stylesheet.css';
import './OutputDisplay'
import OutputDisplay from './OutputDisplay';


function App() {

  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [bmi, setBmi] = useState('');
  const weightAsNumber = Number(weight);
  const heightAsNumber = Number(height);

  const handleClick = () => {var mybmi=weightAsNumber/((heightAsNumber/100)**2);setBmi(mybmi)};


  return (
    <div>
      <h1 style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>BMI Calculator</h1>
      <div style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>
      <label>
        Height(m):
        <input
          value={height}
          onChange={e => setHeight(e.target.value)}
          type="number"
        />
      </label>
      <label>
        Weight:
        <input
          value={weight}
          onChange={e => setWeight(e.target.value)}
          type="number"
        />
        </label>
        </div>
        <div style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>
        <button onClick={() => setWeight(weightAsNumber + 10)}>
          Gain 10 pounds
        </button>
        <button onClick={() => setWeight(weightAsNumber - 10)}>
          Lose 10 pounds
        </button>

        <div style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}></div>
        <button onClick={handleClick}>Calculate</button>
        </div>

      {height !== '' &&
        <p style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>Your height is {heightAsNumber}.</p>
      }
      {weight > 0 &&
        <p style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>Your weight is {weightAsNumber}.</p>
      }
      {weight > 0 &&
        <p style={{display: 'flex',alignItems: 'center',justifyContent: 'center'}}>Your BMI {weightAsNumber/((heightAsNumber/100)**2)}.</p>
      }

    <OutputDisplay title={bmi}></OutputDisplay>
    </div>
  );
}

export default App;
